/*
 * 581. 最短无序连续子数组
 *给你一个整数数组 nums ，你需要找出一个 连续子数组 ，如果对这个子数组进行升序排序，那么整个数组都会变为升序排序。

请你找出符合题意的 最短 子数组，并输出它的长度。

 输入：nums = [2,6,4,8,10,9,15]
输出：5
解释：你只需要对 [6, 4, 8, 10, 9] 进行升序排序，那么整个表都会变为升序排序。

输入：nums = [1,2,3,4]
输出：0
 */
#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

//排序 + 双指针
int findUnsortedSubarray(vector<int>& nums){
    vector<int> temp(nums);
    sort(temp.begin(),temp.end());
    int left = 0, right = 0;
    for(int i = 0; i < nums.size(); ++i){
        if(nums[i] != temp[i]){
            left = i;
            break;
        }
    }
    for (int j = nums.size()-1; j >=0 ; --j) {
        if(nums[j] != temp[j]){
            right = j;
            break;
        }
    }
    return right - left + 1;
}

//双指针 + 线性扫描
int findUnsortedSubarray2(vector<int>& nums){
    int len = nums.size();
    int i = 0, j = len - 1, left = 0, right = 0;

    while(nums[i]<nums[i+1]) ++i;
    while(nums[j]>nums[j-1]) --j;
    left = i;
    right = j;
    if(left >= right){
        return 0;
    }
    cout<< i << j << endl;
    int min = nums[left], max = nums[right];
    for(int k = left; k <= right; ++k){
        if(nums[k] < min){
            while(i >=0 && nums[i] > nums[k]){
                i--;
            }
        }
        if(nums[k] > max){
            while(j < len && nums[j] < nums[k]){
                j++;
            }
        }
    }
    cout<< i << j << endl;

    return (j-1) - (i+1) + 1;
}

//解法3
int findUnsortedSubarray3(vector<int>& nums){
    int len = nums.size();
    int MIN = INT8_MAX , MAX = INT8_MIN;
    int left = -1, right = -1;
    for(int i = 0; i < len; i++){
        if(nums[i] < MAX){
            right = i;
        } else {
            MAX = max(MAX, nums[i]);
        }
        if(nums[len - 1 - i] > MIN){
            left = len - 1 - i;
        } else{
            MIN = min(MIN, nums[len - 1 - i]);
        }
    }
    cout << right << " " << left << " ";
    return right == -1? 0: right - left + 1;
}

